mapper.insert:
insert into
    t_user(
        phone, email, password, salt, createtime
    ) values (
        #{phone}, #{email}, #{password}, #{salt}, #{createtime}
    )

insert into
    t_user_info (
        userid, nick, avatar, sign, gender, birth, createtime
    )
    values (
        #{userid}, #{nick}, #{avatar}, #{sign}, #{gender}, #{birth}, #{createtime}
    )
